import React, { useState } from 'react';
import { userAPI } from '../services/api';
import toast from 'react-hot-toast';
import { User, Mail, Phone, Upload, Loader2 } from 'lucide-react';

const UserRegistration = ({ onUserRegistered }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    profile_picture: null
  });
  const [loading, setLoading] = useState(false);
  const [previewImage, setPreviewImage] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file type
      const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif', 'image/webp'];
      if (!allowedTypes.includes(file.type)) {
        toast.error('Please select a valid image file (JPEG, PNG, GIF, WebP)');
        return;
      }
      
      // Validate file size (5MB)
      if (file.size > 5 * 1024 * 1024) {
        toast.error('File size must be less than 5MB');
        return;
      }

      setFormData(prev => ({
        ...prev,
        profile_picture: file
      }));

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => setPreviewImage(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.name || !formData.email || !formData.phone) {
      toast.error('Please fill in all required fields');
      return;
    }

    setLoading(true);
    try {
      const response = await userAPI.register(formData);
      toast.success('User registered successfully! Check email for confirmation.');
      // Reset form
      setFormData({
        name: '',
        email: '',
        phone: '',
        profile_picture: null
      });
      setPreviewImage(null);
      // Reset file input
      const fileInput = document.getElementById('profile_picture');
      if (fileInput) fileInput.value = '';
      // Notify parent component
      if (onUserRegistered) {
        onUserRegistered(response.user);
      }
    } catch (error) {
      const errorMessage = error.response?.data?.error || 'Registration failed';
      // Show validation error details if present
      if (error.response?.data?.details && Array.isArray(error.response.data.details)) {
        error.response.data.details.forEach((err) => {
          toast.error(err.msg || err.message || JSON.stringify(err));
        });
      } else {
        toast.error(errorMessage);
      }
      console.error('Registration error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card p-6 max-w-md mx-auto">
      <div className="text-center mb-6">
        <div className="mx-auto w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center mb-4">
          <User className="w-6 h-6 text-primary-600" />
        </div>
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Register New User</h2>
        <p className="text-gray-600">Create your account and get email confirmation</p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Name Input */}
        <div className="form-group">
          <label htmlFor="name" className="form-label">
            Full Name *
          </label>
          <div className="relative">
            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              className="input-field pl-10"
              placeholder="Enter your full name"
              required
            />
          </div>
        </div>

        {/* Email Input */}
        <div className="form-group">
          <label htmlFor="email" className="form-label">
            Email Address *
          </label>
          <div className="relative">
            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              className="input-field pl-10"
              placeholder="Enter your email address"
              required
            />
          </div>
        </div>

        {/* Phone Input */}
        <div className="form-group">
          <label htmlFor="phone" className="form-label">
            Phone Number *
          </label>
          <div className="relative">
            <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="tel"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              className="input-field pl-10"
              placeholder="Enter your phone number"
              required
            />
          </div>
        </div>

        {/* Profile Picture Input */}
        <div className="form-group">
          <label htmlFor="profile_picture" className="form-label">
            Profile Picture
          </label>
          <div className="mt-1 flex items-center space-x-4">
            {previewImage ? (
              <img
                src={previewImage}
                alt="Profile preview"
                className="w-16 h-16 rounded-full object-cover border-2 border-gray-200"
              />
            ) : (
              <div className="w-16 h-16 rounded-full bg-gray-100 flex items-center justify-center border-2 border-gray-200">
                <User className="w-8 h-8 text-gray-400" />
              </div>
            )}
            <label
              htmlFor="profile_picture"
              className="cursor-pointer bg-white py-2 px-3 border border-gray-300 rounded-md shadow-sm text-sm leading-4 font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
            >
              <Upload className="w-4 h-4 inline mr-2" />
              Choose File
            </label>
            <input
              type="file"
              id="profile_picture"
              name="profile_picture"
              onChange={handleFileChange}
              accept="image/*"
              className="hidden"
            />
          </div>
          <p className="text-xs text-gray-500 mt-1">
            PNG, JPG, GIF up to 5MB
          </p>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          disabled={loading}
          className="w-full btn-primary flex items-center justify-center"
        >
          {loading ? (
            <>
              <Loader2 className="animate-spin w-5 h-5 mr-2" />
              Registering...
            </>
          ) : (
            'Register User'
          )}
        </button>
      </form>
    </div>
  );
};

export default UserRegistration;
