import React, { useState } from 'react';
import { Toaster } from 'react-hot-toast';
import UserRegistration from './components/UserRegistration';
import UserList from './components/UserList';
import { Users, UserPlus, Database, Github } from 'lucide-react';

function App() {
  const [activeTab, setActiveTab] = useState('register');
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  const handleUserRegistered = () => {
    // Trigger refresh of user list
    setRefreshTrigger(prev => prev + 1);
    // Switch to user list tab
    setActiveTab('users');
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Toast notifications */}
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
          success: {
            style: {
              background: '#10b981',
            },
          },
          error: {
            style: {
              background: '#ef4444',
            },
          },
        }}
      />

      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center">
              <div className="flex items-center">
                <Database className="w-8 h-8 text-primary-600 mr-3" />
                <div>
                  <h1 className="text-2xl font-bold text-gray-900">
                    User Management System
                  </h1>
                  <p className="text-sm text-gray-600">
                    Full-stack application with React, Node.js, Express & MySQL
                  </p>
                </div>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <a
                href="https://github.com"
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-400 hover:text-gray-600 transition-colors"
              >
                <Github className="w-6 h-6" />
              </a>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation Tabs */}
      <nav className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8">
            <button
              onClick={() => setActiveTab('register')}
              className={`py-4 px-1 border-b-2 font-medium text-sm flex items-center transition-colors ${
                activeTab === 'register'
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <UserPlus className="w-5 h-5 mr-2" />
              Register User
            </button>
            <button
              onClick={() => setActiveTab('users')}
              className={`py-4 px-1 border-b-2 font-medium text-sm flex items-center transition-colors ${
                activeTab === 'users'
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <Users className="w-5 h-5 mr-2" />
              Manage Users
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {activeTab === 'register' && (
          <div className="animate-fade-in">
            <UserRegistration onUserRegistered={handleUserRegistered} />
          </div>
        )}
        
        {activeTab === 'users' && (
          <div className="animate-fade-in">
            <UserList refreshTrigger={refreshTrigger} />
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <div className="flex items-center mb-4 md:mb-0">
              <Database className="w-6 h-6 text-primary-600 mr-2" />
              <span className="text-gray-600">User Management System</span>
            </div>
            
            <div className="text-sm text-gray-500">
              <p>Built with React.js, Node.js, Express.js, MySQL & TailwindCSS</p>
            </div>
          </div>
          
          <div className="mt-6 pt-6 border-t border-gray-200">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-sm text-gray-600">
              <div>
                <h4 className="font-semibold text-gray-900 mb-2">Features</h4>
                <ul className="space-y-1">
                  <li>• User Registration with Profile Picture</li>
                  <li>• Email Confirmation with Nodemailer</li>
                  <li>• Complete CRUD Operations</li>
                  <li>• REST API with Express.js</li>
                </ul>
              </div>
              
              <div>
                <h4 className="font-semibold text-gray-900 mb-2">Technologies</h4>
                <ul className="space-y-1">
                  <li>• Frontend: React.js + TailwindCSS</li>
                  <li>• Backend: Node.js + Express.js</li>
                  <li>• Database: MySQL</li>
                  <li>• File Upload: Multer</li>
                </ul>
              </div>
              
              <div>
                <h4 className="font-semibold text-gray-900 mb-2">Security</h4>
                <ul className="space-y-1">
                  <li>• Input Validation</li>
                  <li>• File Type Restrictions</li>
                  <li>• CORS Protection</li>
                  <li>• Error Handling</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
